import React, { useEffect, useState } from "react";

export default function MentorFormModal({ isOpen, onClose, onSave, initialData = null }) {
  const [name, setName] = useState("");
  const [specialty, setSpecialty] = useState("");
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("Activo");
  const [photo, setPhoto] = useState("");

  useEffect(() => {
    if (initialData) {
      setName(initialData.name || "");
      setSpecialty(initialData.specialty || "");
      setTitle(initialData.title || "");
      setStatus(initialData.status || "Activo");
      setPhoto(initialData.photo || "");
    } else {
      setName("");
      setSpecialty("");
      setTitle("");
      setStatus("Activo");
      setPhoto("");
    }
  }, [initialData, isOpen]);

  if (!isOpen) return null;

  function handleSubmit(e) {
    e.preventDefault();
    const payload = {
      // if editing, initialData likely has id and we keep it
      ...(initialData && initialData.id ? { id: initialData.id } : {}),
      name: name.trim(),
      specialty: specialty.trim(),
      title: title.trim(),
      status,
      photo: photo.trim()
    };
    if (!payload.name) {
      alert("El nombre es requerido");
      return;
    }
    onSave(payload);
    // don't call onClose here because the parent will usually close after save, but ensure UX: close anyway
    onClose();
  }

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center">
      <div className="absolute inset-0 bg-black/50" onClick={onClose} />

      <div className="relative bg-white w-full max-w-2xl mx-4 rounded-lg shadow-lg z-10">
        <div className="flex items-center justify-between px-6 py-4 border-b">
          <h3 className="text-lg font-semibold text-gray-800">
            {initialData ? "Editar Mentor" : "Añadir Nuevo Mentor"}
          </h3>
          <button onClick={onClose} className="p-2 rounded-md hover:bg-gray-100">
            <svg className="w-5 h-5 text-gray-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <form onSubmit={handleSubmit} className="px-6 py-6 space-y-4">
          <div>
            <label className="block text-sm text-gray-600">Nombre completo</label>
            <input
              value={name}
              onChange={(e) => setName(e.target.value)}
              type="text"
              placeholder="Ej. Ana Fernández"
              className="mt-1 w-full rounded-md border-gray-200 shadow-sm focus:border-primary focus:ring focus:ring-primary/20"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-600">Especialidad</label>
            <input
              value={specialty}
              onChange={(e) => setSpecialty(e.target.value)}
              type="text"
              placeholder="Ej. Ciberseguridad, Desarrollo"
              className="mt-1 w-full rounded-md border-gray-200 shadow-sm focus:border-primary focus:ring focus:ring-primary/20"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-600">Título / Cargo</label>
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              type="text"
              placeholder="Ej. Arquitecto de Soluciones"
              className="mt-1 w-full rounded-md border-gray-200 shadow-sm focus:border-primary focus:ring focus:ring-primary/20"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-600">Foto (URL)</label>
            <input
              value={photo}
              onChange={(e) => setPhoto(e.target.value)}
              type="text"
              placeholder="https://..."
              className="mt-1 w-full rounded-md border-gray-200 shadow-sm focus:border-primary focus:ring focus:ring-primary/20"
            />
          </div>

          <div>
            <label className="block text-sm text-gray-600">Estado</label>
            <select
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              className="mt-1 w-full rounded-md border-gray-200 shadow-sm focus:border-primary focus:ring focus:ring-primary/20"
            >
              <option>Activo</option>
              <option>Inactivo</option>
            </select>
          </div>

          <div className="pt-4 border-t flex items-center justify-end gap-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 rounded-md border text-gray-600 hover:bg-gray-50"
            >
              Cancelar
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-primary text-white rounded-md hover:bg-primaryDark"
            >
              Guardar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
