/**
 * Servicio de Mentores - Consume la API en /api/mentors
 * Maneja GET, PUT, DELETE de mentores
 */

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:5000/api";

/**
 * Maneja respuestas de la API
 */
async function handleResponse(res) {
  const text = await res.text();
  let payload;
  try {
    payload = text ? JSON.parse(text) : null;
  } catch (e) {
    payload = text;
  }
  if (!res.ok) {
    const errorMsg = payload?.error || payload?.message || payload || "API error";
    throw new Error(errorMsg);
  }
  return payload;
}

/**
 * GET /api/mentors
 * Obtiene todos los mentores
 */
export async function getAllMentors() {
  try {
    const res = await fetch(`${API_URL}/mentors`);
    return await handleResponse(res);
  } catch (error) {
    console.error("Error fetching mentors:", error);
    throw error;
  }
}

/**
 * GET /api/mentors/:id
 * Obtiene un mentor específico por ID
 */
export async function getMentorById(id) {
  if (!id) throw new Error("Mentor ID is required");
  try {
    const res = await fetch(`${API_URL}/mentors/${id}`);
    return await handleResponse(res);
  } catch (error) {
    console.error(`Error fetching mentor ${id}:`, error);
    throw error;
  }
}

/**
 * POST /api/mentors
 * Crea un nuevo mentor
 * 
 * Estructura esperada:
 * {
 *   experience: string,
 *   schedules: array,
 *   languages: array,
 *   certificates: array,
 *   user: { id, name, email, ... },
 *   id_area: number,
 *   id_pedagogicalMethod: number
 * }
 */
export async function createMentor(data) {
  if (!data) throw new Error("Mentor data is required");
  try {
    const res = await fetch(`${API_URL}/mentors`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data)
    });
    return await handleResponse(res);
  } catch (error) {
    console.error("Error creating mentor:", error);
    throw error;
  }
}

/**
 * PUT /api/mentors/:id
 * Actualiza un mentor existente
 */
export async function updateMentor(id, data) {
  if (!id) throw new Error("Mentor ID is required");
  if (!data) throw new Error("Mentor data is required");
  try {
    const res = await fetch(`${API_URL}/mentors/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data)
    });
    return await handleResponse(res);
  } catch (error) {
    console.error(`Error updating mentor ${id}:`, error);
    throw error;
  }
}

/**
 * DELETE /api/mentors/:id
 * Elimina un mentor
 */
export async function deleteMentor(id) {
  if (!id) throw new Error("Mentor ID is required");
  try {
    const res = await fetch(`${API_URL}/mentors/${id}`, {
      method: "DELETE"
    });
    if (!res.ok) {
      const error = await res.text();
      throw new Error(error || "Failed to delete mentor");
    }
    return true;
  } catch (error) {
    console.error(`Error deleting mentor ${id}:`, error);
    throw error;
  }
}

export default {
  getAllMentors,
  getMentorById,
  createMentor,
  updateMentor,
  deleteMentor
};
